import { useState } from "react";
import { motion } from "framer-motion";
import "./App.css";

function App() {
  const [prompt, setPrompt] = useState("");
  const [useCase, setUseCase] = useState("interior");
  const [concepts, setConcepts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  async function handleGenerate() {
    setLoading(true);
    setError("");
    setConcepts([]);
    try {
      const res = await fetch("/api/generate", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ prompt, useCase })
      });

      if (!res.ok) {
        const errData = await res.json();
        throw new Error(errData.error || "Backend error");
      }

      const data = await res.json();
      setConcepts(data.concepts || []);
    } catch (err) {
      console.error(err);
      setError("Error: " + err.message);
    } finally {
      setLoading(false);
    }
  }

  return (
    <div className="container">
      <motion.h1 
        className="title"
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
      >
        ðŸŽ¨ Voice-to-Design AI Studio
      </motion.h1>

      {/* Form Box */}
      <motion.div 
        className="form-box"
        initial={{ scale: 0.9, opacity: 0 }}
        animate={{ scale: 1, opacity: 1 }}
        transition={{ duration: 0.6 }}
      >
        <label>Select Use Case</label>
        <select value={useCase} onChange={(e) => setUseCase(e.target.value)}>
          <option value="interior">Interior</option>
          <option value="architecture">Architecture</option>
          <option value="construction">Construction</option>
          <option value="event">Event</option>
        </select>

        <label style={{ marginTop: "15px", display: "block" }}>Enter Prompt</label>
        <textarea
          value={prompt}
          onChange={(e) => setPrompt(e.target.value)}
          placeholder="e.g., A futuristic dome-shaped office with sustainable design..."
        />

        <button onClick={handleGenerate} disabled={loading}>
          {loading ? "âœ¨ Generating..." : "Generate 3 Concepts"}
        </button>
      </motion.div>

      {error && <p style={{ color: "red" }}>{error}</p>}

      {/* Results */}
      <div className="results-grid">
        {concepts.map((c, idx) => (
          <motion.div
            key={idx}
            className="card"
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: idx * 0.2 }}
          >
            <h3>{c.title}</h3>
            <p>{c.summary}</p>
            <ul>
              {c.highlights.map((h, i) => (
                <li key={i}>{h}</li>
              ))}
            </ul>
          </motion.div>
        ))}
      </div>
    </div>
  );
}

export default App;
