import { useState } from "react";
import { motion } from "framer-motion";
import "./App.css";

function App() {
  const [prompt, setPrompt] = useState("");
  const [useCase, setUseCase] = useState("interior");
  const [result, setResult] = useState(""); // store generated image
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  async function handleGenerate() {
    setLoading(true);
    setError("");
    setResult("");

    try {
      const res = await fetch("/api/generate-image", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ prompt: `${useCase} design: ${prompt}` })
      });

      if (!res.ok) {
        const errData = await res.json();
        throw new Error(errData.error || "Backend error");
      }

      const data = await res.json();
      setResult(data.image); // base64 image from backend
    } catch (err) {
      console.error(err);
      setError("Error: " + err.message);
    } finally {
      setLoading(false);
    }
  }

  return (
    <div className="container">
      <motion.h1 
        className="title"
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
      >
        ðŸŽ¨ Voice-to-Design AI Studio
      </motion.h1>

      {/* Form Box */}
      <motion.div 
        className="form-box"
        initial={{ scale: 0.9, opacity: 0 }}
        animate={{ scale: 1, opacity: 1 }}
        transition={{ duration: 0.6 }}
      >
        <label>Select Use Case</label>
        <select value={useCase} onChange={(e) => setUseCase(e.target.value)}>
          <option value="interior">Interior</option>
          <option value="architecture">Architecture</option>
          <option value="construction">Construction</option>
          <option value="event">Event</option>
        </select>

        <label style={{ marginTop: "15px", display: "block" }}>Enter Prompt</label>
        <textarea
          value={prompt}
          onChange={(e) => setPrompt(e.target.value)}
          placeholder="e.g., A futuristic dome-shaped office with sustainable design..."
        />

        <button onClick={handleGenerate} disabled={loading}>
          {loading ? "âœ¨ Generating..." : "Generate Design"}
        </button>
      </motion.div>

      {error && <p style={{ color: "red" }}>{error}</p>}

      {/* Result Image */}
      {result && (
        <motion.div
          className="card"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.6 }}
          style={{ marginTop: "20px" }}
        >
          <h3>Generated Design</h3>
          <img 
            src={result} 
            alt="AI Generated" 
            style={{ maxWidth: "100%", borderRadius: "12px" }} 
          />
        </motion.div>
      )}
    </div>
  );
}

export default App;
