import { useState, useRef } from "react";
import "./App.css";

function App() {
  const [prompt, setPrompt] = useState("");
  const [count, setCount] = useState(3);
  const [listening, setListening] = useState(false);

  const recognitionRef = useRef(null);

  // ðŸŽ¤ Setup speech recognition
  function initSpeechRecognition() {
    const SpeechRecognition =
      window.SpeechRecognition || window.webkitSpeechRecognition;
    if (!SpeechRecognition) {
      alert("Your browser does not support Speech Recognition. Try Chrome.");
      return null;
    }

    const recognition = new SpeechRecognition();
    recognition.lang = "en-US";
    recognition.continuous = false;
    recognition.interimResults = false;

    recognition.onstart = () => setListening(true);
    recognition.onend = () => setListening(false);

    recognition.onresult = (event) => {
      const transcript = event.results[0][0].transcript;
      setPrompt((prev) => prev + " " + transcript);
    };

    return recognition;
  }

  function handleMicClick() {
    if (listening && recognitionRef.current) {
      recognitionRef.current.stop();
      return;
    }
    recognitionRef.current = initSpeechRecognition();
    if (recognitionRef.current) {
      recognitionRef.current.start();
    }
  }

  function handleGenerate() {
    alert(`Generating ${count} concepts for: "${prompt}"`);
    // Here you can call backend API
  }

  return (
    <div className="app">
      {/* Header */}
      <header className="header">Voice to Design</header>

      {/* Prompt Section */}
      <div className="main-box">
        <label>Enter Prompt</label>
        <div className="input-row">
          <input
            type="text"
            value={prompt}
            onChange={(e) => setPrompt(e.target.value)}
            placeholder="Type or speak your idea..."
          />
          <button
            onClick={handleMicClick}
            className={`mic-btn ${listening ? "recording" : ""}`}
          >
            ðŸŽ¤
          </button>
        </div>

        {/* Count + Generate */}
        <div className="actions">
          <input
            type="number"
            value={count}
            min="1"
            max="5"
            onChange={(e) => setCount(Number(e.target.value))}
          />
          <button onClick={handleGenerate}>Generate</button>
        </div>
      </div>

      {/* Footer */}
      <footer className="footer">
        Designed & Developed by <b>Chethan.Kl</b>
      </footer>
    </div>
  );
}

export default App;
